import React, { useState }  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField'


export default function ProblemCard(props) {
    const classes = useStyles();
    const question = props.question;
    var inputVal = "";
    const [color, changeColor] = useState('');
    const [isCorrect, answerIs] = useState(true);

    function submit() {
        if (inputVal === question.partAnswer) {
            answerIs(true)
            changeColor('green')
        } else {
            answerIs(false)
            changeColor('')
        }
    }

    function handleChange(event) {
        inputVal = event.target.value
    }

    const textFieldStyle = {outlineColor: color};

    return (
        <Card className={classes.card} style={textFieldStyle}>
            <CardContent>
                <h2 className={classes.questionHeader}>
                    {question.partTitle}
                    <hr/>
                </h2>

                <div className = {classes.questionBody}>
                    {question.partBody}
                </div>

                <TextField error={!isCorrect} className={classes.inputField} variant="outlined" onChange = {(evt) => handleChange(evt)}>hi</TextField>



            </CardContent>
            <CardActions>
                <Button className={classes.button} size="small" onClick={submit}>Submit</Button>
            </CardActions>
        </Card>
    );
}

const useStyles = makeStyles({
    card: {
        width: '40%',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginTop: 20
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },

    button: {
        backgroundColor: '#8c94ff',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: 10,
        paddingRight: 10,
        width: "20%"
    },

    questionHeader: {
        textAlign: 'center',
        fontSize: 20,
        marginTop: 0,
    },

    questionBody: {
        textAlign: 'center',
        fontSize: 30,
        marginTop: 10,
        marginBottom: 30
    },

    inputField: {
        marginLeft: '35%',
        width: '30%',

    }


});
