import React, { useState }  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ProblemCard from './ProblemCard'
import {animateScroll as scroll, scroller, Element} from "react-scroll";
import {update, knowledgeComponents} from '../BKT/BKTBrains'

export default function Problem(props) {
    const [problemIndex, updateIndex] = useState(0);
    const problemData = props.problems[problemIndex];
    const numProblems = props.problems.length;
    var partStates = {};
    var numCorrect = 0;


    const parts = problemData.parts.map(function(part, index) {
        partStates[index] = null;
        return <Element name={index.toString()} key={problemIndex.toString() + index.toString()}>
            <ProblemCard part={part} index={index} answerMade = {answerMade}/>
        </Element>
    }
    );

    function answerMade(cardIndex, kcArray, isCorrect) {
        if (partStates[cardIndex] === true) { return }
        partStates[cardIndex] = isCorrect;

        for (kc in kcArray) {
            update(knowledgeComponents[kc], isCorrect);
            console.log(knowledgeComponents[kc].probMastery);
        }


        if (isCorrect) {
            numCorrect += 1;
            if (numCorrect === Object.keys(partStates).length) {
                scroll.scrollToTop({duration: 900});
                partStates = {};
                numCorrect = 0;
                if (problemIndex < numProblems - 1) {
                    setTimeout(function(){ updateIndex(problemIndex + 1); }, 900)
                }
            } else {
                scroller.scrollTo((cardIndex + 1).toString(), {
                    duration: 500,
                    smooth: true
                })
            }
        }
    }

    const classes = useStyles();

    return (
        <div>
            <div className={classes.prompt}>
                {problemData.prompt}
                <hr/>

            </div>
            {parts}
        </div>
    );
}

const useStyles = makeStyles({
    prompt: {
        marginLeft: 50,
        marginRight: 50,
        marginTop: 20,
        textAlign: 'center',
        fontSize: 30,
        fontFamily: 'Titillium Web, sans-serif',
    },
});
