import React  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ProblemCard from './ProblemCard'

export default function Problem(props) {

    const problemData = props.problem;
    const questions = problemData.parts.map((question) =>
        <ProblemCard question={question}/>
    );
    const problemPrompt = problemData.prompt.replace(/(?:\r\n|\r|\n)/g, '<br>');
    const classes = useStyles();

    return (
        <div>
            <div className={classes.prompt}>
                <h1>{problemPrompt}</h1>
                <hr/>
            </div>


            {questions}
        </div>
    );
}

const useStyles = makeStyles({
    prompt: {
        paddingLeft: 50,
        paddingRight: 50,
        textAlign: 'center',
        fontSize: 15,
        fontFamily: 'Titillium Web, sans-serif',
    },
});
