import React, { useState }  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ProblemCard from './ProblemCard'
import {animateScroll as scroll, scroller, Element} from "react-scroll";

export default function Problem(props) {
    const [problemIndex, updateIndex] = useState(0);
    const problemData = props.problems[problemIndex];
    var questionStates = {};
    var numCorrect = 0;


    const questions = problemData.parts.map(function(question, index) {
        questionStates[index] = false;
        return <Element name={index.toString()} key={problemIndex.toString() + index.toString()}>
            <ProblemCard question={question} index={index} answerMade = {answerMade}/>
        </Element>
    }
    );

    function answerMade(cardIndex, isCorrect) {
        if (questionStates[cardIndex] === isCorrect) { return }
        questionStates[cardIndex] = isCorrect;

        if (isCorrect) {
            numCorrect += 1;
            if (numCorrect === Object.keys(questionStates).length) {
                scroll.scrollToTop({duration: 500});
                questionStates = {};
                numCorrect = 0;
                setTimeout(function(){ updateIndex(problemIndex + 1); }, 500)
            } else {
                scroller.scrollTo((cardIndex + 1).toString(), {

                    })
            }
        } else {
            numCorrect -= 1
        }


    }

    const classes = useStyles();

    return (
        <div>
            <div className={classes.prompt}>
                {problemData.prompt}
                <hr/>

            </div>
            {questions}
        </div>
    );
}

const useStyles = makeStyles({
    prompt: {
        marginLeft: 50,
        marginRight: 50,
        marginTop: 20,
        textAlign: 'center',
        fontSize: 30,
        fontFamily: 'Titillium Web, sans-serif',
    },
});
