import React, { useState }  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField'


export default function ProblemCard(props) {
    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;
    const question = props.question;
    var inputVal = ""
    const [color, changeColor] = useState("");

    function submit() {
        console.log(inputVal)
        if (inputVal === question.partAnswer) {
            changeColor("green")
        } else {
            changeColor("red")
        }
    }

    function handleChange(event) {
        inputVal = event.target.value
    }

    return (
        <Card className={classes.card}>
            <CardContent>
                <h2 className={classes.questionHeader}>
                    {question.partTitle}
                    {color}
                    <hr/>
                </h2>

                <div className = {classes.questionBody}>
                    {question.partBody}
                </div>

                <TextField style={{backgroundColor: {color}}} className={classes.inputField} variant="outlined" onChange = {(evt) => handleChange(evt)}></TextField>


            </CardContent>
            <CardActions>
                <Button className={classes.button} size="small" onClick={submit}>Submit</Button>
            </CardActions>
        </Card>
    );
}

const useStyles = makeStyles({
    card: {
        width: '40%',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginTop: 20
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
